import React, { useEffect } from "react";
import NavBar from "../Components/NavBar";
import { useParams } from "react-router-dom";
import { publicRequest } from "../features/requestMethod";

const BlogPage = () => {


    const {id} = useParams()

    const [post,setPost]=useState(null)

    const getBlog=async()=>{
        try{
            const res = await publicRequest(`/posts/${id}`)
            if(res.status){
                setPost(res.data)
            }
        }
        catch(err){

        }
    }

    useEffect(()=>{

    },[])

    const image = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQKDiswMiDfmxna3cGMdQ4EOrcHhz4VK1mEEw&s"
    const author = "Rishab Bibhuty"
    const content = "The ongoing Russo-Ukrainian War began in February 2014. Following Ukraine's Revolution of Dignity, Russia occupied and annexed Crimea from Ukraine and supported pro-Russian separatists fighting the Ukrainian military in the Donbas War. These first eight years of conflict also included naval incidents and cyberwarfare"
    const title = "Russia-ukraine war"




  return (
    <>
      <NavBar />
      <div>
        <div className="w-full max-w-4xl mx-auto p-4">
          <div className="w-full">
            <img
              className="w-full h-auto object-cover rounded-lg"
              src={image}
              alt="Blog post"
            />
          </div>
          <div className="mt-6">
            <h1 className="text-4xl font-bold">{title}</h1>
            <p className="text-gray-500 mt-2">
              By {author}
            </p>
          </div>
          <div className="mt-6">
            <p className="text-lg leading-relaxed text-gray-800">{content}</p>
          </div>
        </div>
      </div>
    </>
  );
};

export default BlogPage;
