import { publicRequest } from './requestMethod'
import {loginFailure,loginStart,loginSuccess } from '../features/currUserSlice'




export const LoginUser = async (dispatch,user)=>{
        try{
        dispatch(loginStart())
        const res = await publicRequest.get(`user/login?username=${user.username}&password=${user.password}`)
        dispatch(loginSuccess(res.data))
        // localStorage.setItem("user",res.data.username)
        }
        catch(err){
            dispatch(loginFailure())
        }
}




export const RegisterUser = async (dispatch,user)=>{
    try{
    const res = await publicRequest.post(`user/register`,user)
    if(res.status == 201){
        return true
    }
    }
    catch(err){
        console.log(err)
    }
}
