import React, { useState } from 'react'
import NavBar from '../Components/NavBar'
import CreatePost from '../Components/CreatePost'
import Blog from '../Components/Blog'

const UserDashborad = () => {
  const [flag,setFlag] = useState(false)
  let userPost = ['1','2','3']

  const handleClick=(type)=>{
     if(type == "post")
     setFlag(!flag)
  }

  return (
        <>
        <NavBar />
        <div className=''>
            <div className='flex p-4 space-x-2'>
            <button
                onClick={() => handleClick("post")}
               className="p-3 text-sm bg-orange-400 font-bold font-BebasNeue tracking-[2px] hover:scale-105 hover:transition hover:duration-150 ease-out  text-white uppercase "
              >
                    Your Blog
              </button>
              <button
                onClick={() => handleClick("createPost")}
               className="p-3 text-sm bg-orange-400 font-bold font-BebasNeue tracking-[2px] hover:scale-105 hover:transition hover:duration-150 ease-out  text-white uppercase "
              >
                Create a Post
              </button>
            </div>

            <div className='p-1'>
                   { 
                    flag ?

                    // create post section
                    <div>
                         <CreatePost />
                    </div>    
                    :

                    // user post section                   
                    <div>
                        {
                            userPost.map((post,index)=>(
                                <div key={index}>
                                    <Blog />
                                </div>       
                            ))
                        }
                    </div>
                   }   
            </div>
        </div>
        </>
  )
}

export default UserDashborad
